    context.read<TripListDetailsBloc>().commentTextEmpty = false;
    reasonSearchFieldController.clear();
    context.read<TripListDetailsBloc>().selectedReason = "";
    context.read<TripListDetailsBloc>().selectedReasonName = null;
    commentsBar.clear();
    context.read<TripListDetailsBloc>().selectedForSortedItem = selectedItem;
    return BlocConsumer<TripListDetailsBloc, TripListDetailsState>(
      listenWhen: (TripListDetailsState previous, TripListDetailsState current) {
        return previous != current;
      },
      buildWhen: (TripListDetailsState previous, TripListDetailsState current) {
        return previous != current;
      },
      listener: (BuildContext context, TripListDetailsState state) {
        if (state is TripListDetailsSuccess) {
          context.read<TripListDetailsBloc>().updateLoader = false;
          Navigator.pop(context);
          ScaffoldMessenger.of(context).clearSnackBars();
          if (state.message != "") {
            ScaffoldMessenger.of(context).showSnackBar(SnackBar(content: Text(state.message)));
          }
        }
        if (state is TripListDetailsFailure) {
          context.read<TripListDetailsBloc>().updateLoader = false;
          context.read<TripListDetailsBloc>().add(const TripListDetailsSetStateEvent());
        }
        if (state is TripListDetailsError) {
          context.read<TripListDetailsBloc>().updateLoader = false;
          ScaffoldMessenger.of(context).clearSnackBars();
          getIt<Widgets>().flushBarWidget(context: context, message: state.message);
        }
